mapper.select:
SELECT t.course_id, t.course_title, t.course_url AS course_image, '0' AS type, t.create_time, t.student_num AS view_num, '' AS comment_num, t.series_course_updown AS 'status', t.course_updown AS is_single FROM `t_courses` t WHERE t.series_id = #{series_id}
t.goods_type = #{series_id}
ORDER BY t.create_time DESC

SELECT t.course_id, t.course_title, t.course_url AS course_image, t.create_time, t.course_price, t.course_amount, t.student_num AS sale_num, t.course_updown, (t.start_time < SYSDATE()) AS is_start, t.end_time FROM t_course t WHERE t.lecturer_id = #{user_id}
and t.course_title like '%${keyword}%'
AND course_updown = #{course_updown}
AND course_updown !=0
AND live_course_status !=3 ORDER BY t.create_time DESC

select
  *
from t_course res
where res.shop_id = #{shop_id,jdbcType=VARCHAR}

  and course_updown = #{status,jdbcType=CHAR}


  and goods_type = #{type,jdbcType=CHAR}


  and course_title like '%${keyword}%'


  and res.series_course_updown = '0'

AND  res.course_updown != '0'
order by res.course_updown , res.updown_time desc
