mapper.select:
select a.*,b.nick_name,b.avatar_address from t_fans a, t_user b where a.user_id=b.user_id and a.room_id=#{room_id}
and a.lecturer_id=#{lecturer_id}

  and a.position  >  #{position}


order by a.position limit #{page_count}

select a.*,b.m_user_id,b.m_pwd,b.web_openid from t_fans a, t_login_info b where a.user_id=b.user_id and a.room_id=#{room_id}
and a.lecturer_id=#{lecturer_id}

  and a.position  >  #{position}


order by a.position limit #{page_count}

select a.*, b.room_name, b.avatar_address from t_fans a, t_live_room b
where a.room_id=b.room_id and a.user_id = #{user_id}

  and a.create_time  <  #{notice_create_time}

order by a.create_time desc limit #{page_count}

mapper.delete:
delete from t_fans where 1 = 1 

	and user_id = #{user_id}


	and room_id = #{room_id}
