mapper.select:
select a.*,ifnull(b.nick_name,b.user_name) student from t_lecturer_courses_profit a, t_user b where a.course_id=#{course_id} and a.user_id=b.user_id

	and a.position  <  #{position}


	and a.profit_type=#{profit_type} 

order by a.position desc limit #{page_count}

select
lcp.profit_type, lcp.course_id, lcp.profit_amount, lcp.profit_amount,
lcp.create_time, lcp.position, t_courses.course_title, lcp.lecturer_id
from t_lecturer_courses_profit lcp left join t_courses  on t_courses.course_id = lcp.course_id
where 1=1
and user_id = #{user_id}

 and  `position` < #{position}

order by `position` desc
limit #{page_count}

select
*
from t_lecturer_courses_profit
where 1=1
and user_id = #{user_id}
and distributer_id = #{distributer_id}
and room_id = #{room_id}
and rq_code = #{rq_code}
limit 1
