mapper.delete:
delete from t_saas_banner
where banner_id = #{banner_id,jdbcType=VARCHAR}
and user_id = #{user_id,jdbcType=VARCHAR}

mapper.select:
    select 
    from t_saas_banner


			and shop_id = #{shop_id}


			and status = #{status}


			and app_name = #{app_name}


    order by position asc
