mapper.select:
select

from t_saas_course res
where res.shop_id = #{shop_id,jdbcType=VARCHAR}

  and course_updown = #{status,jdbcType=CHAR}


  and goods_type = #{type,jdbcType=CHAR}


  and course_title like '%${keyword}%'

SELECT t.course_id, t.course_title, t.course_updown AS status FROM `t_saas_course` t WHERE t.shop_id = #{shop_id,jdbcType=VARCHAR} AND t.course_updown = '1' AND series_or_course = '0' AND goods_type = #{type,jdbcType=VARCHAR}
course_title like '%${keyword}%'
ORDER BY t.update_time DESC
