mapper.update:
update t_saas_shop_users


    update_time = #{update_time},


    msg_num = msg_num + #{comment_num,jdbcType=BIGINT},


    where shop_id = #{shop_id,jdbcType=VARCHAR}
      and user_id = #{user_id,jdbcType=VARCHAR}

update t_saas_shop_users
 set user_type = 1,
 total_consume = #{totalConsume,jdbcType=BIGINT}
where shop_id = #{shopId,jdbcType=VARCHAR}
AND user_id = #{userId,jdbcType=VARCHAR}

mapper.select:
SELECT t.shop_id, t.user_id, t.user_type, t.total_consume, t.msg_num, u.nick_name, u.avatar_address, u.gender FROM `t_saas_shop_users` t LEFT JOIN t_user u ON t.user_id = u.user_id WHERE t.shop_id = #{shop_id,jdbcType=VARCHAR}
and u.nick_name like '%${nick_name}%'
and t.user_type = #{type,jdbcType=VARCHAR}
and u.phone_number like '%${phone}%'
ORDER BY t.create_time desc
