mapper.select:
select * from t_withdraw_cash where user_id = #{user_id}

   and state = #{state}


   and withdraw_cash_id = #{withdraw_cash_id}

 order by create_time desc limit #{page_num},#{page_count}

select user_id, SUM(actual_amount) sum from t_withdraw_cash where `state` = 1
#{userId}
GROUP BY user_id

SELECT t.actual_amount, t.create_time, t.state as status, t.remark FROM `t_withdraw_cash` t WHERE t.user_id = #{userId}
AND t.create_time > #{create_time}
ORDER BY t.create_time DESC limit #{page_count}

SELECT t.withdraw_cash_id, t.user_id, t.user_name, t.nick_name, t.actual_amount, t.alipay_account_number, t.create_time, t.state AS status, t.remark, t.handle_name FROM `t_withdraw_cash` t WHERE app_name = #{app_name}
AND t.state = #{status}
AND t.user_name like '%${user_name}%'
AND t.user_id = #{user_name}
ORDER BY t.state=0 desc, t.create_time DESC
