mapper.insert:
insert into t_lecturer (lecturer_id, course_num, total_student_num,
pay_student_num, total_time, live_room_num,
fans_num, pay_course_num, private_course_num,
total_amount, create_time, update_time
)
values (#{lecturerId,jdbcType=VARCHAR}, #{courseNum,jdbcType=BIGINT}, #{totalStudentNum,jdbcType=BIGINT},
#{payStudentNum,jdbcType=BIGINT}, #{totalTime,jdbcType=BIGINT}, #{liveRoomNum,jdbcType=BIGINT},
#{fansNum,jdbcType=BIGINT}, #{payCourseNum,jdbcType=BIGINT}, #{privateCourseNum,jdbcType=BIGINT},
#{totalAmount,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
)

mapper.select:
select lecturer_id from t_lecturer 

    limit #{start_pos}


    , #{page_count}


	limit #{page_count}
