values (#{fansId,jdbcType=VARCHAR}, #{lecturerId,jdbcType=VARCHAR}, #{roomId,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{createDate,jdbcType=DATE}, #{position,jdbcType=BIGINT}
  )

mapper.update:
